    private bool _isChatting = true;
    private string _username;
    private string _newMessage;
    private List<Message> _messages = new List<Message>();

    private string _hubUrl;
    private HubConnection _hubConnection;
    protected override async Task OnInitializedAsync()
    {
        if(_httpContextAccessor.HttpContext.User.Identity.IsAuthenticated)
        {
            _username =_httpContextAccessor.HttpContext.User.FindFirst(ClaimTypes.Name).Value;
            _isChatting = true;
            _messages = GetMessages();
            
            await Task.Delay(1);

            string baseUrl = navigationManager.BaseUri;

            _hubUrl = baseUrl.TrimEnd('/') + BlazorChatSampleHub.HubUrl;

            _hubConnection = new HubConnectionBuilder()
                .WithUrl(_hubUrl)
                .Build();

            _hubConnection.On<string, string>("Broadcast", BroadcastMessage);

            await _hubConnection.StartAsync();
            await SendAsync($"[Notice] {_username} joined chat room.");
        }
    }
 

    private void BroadcastMessage(string name, string message)
    {
        bool isMine = name.Equals(_username, StringComparison.OrdinalIgnoreCase);

        _messages.Add(new Message(name, message, isMine));

        StateHasChanged();
    }

    private async Task DisconnectAsync()
    {
        if (_isChatting)
        {
            await SendAsync($"[Notice] {_username} left chat room.");

            await _hubConnection.StopAsync();
            await _hubConnection.DisposeAsync();

            _hubConnection = null;
            navigationManager.NavigateTo("/");
        }
    }

    private async Task SendAsync(string message)
    {
        if (_isChatting && !string.IsNullOrWhiteSpace(message))
        {
            await _hubConnection.SendAsync("Broadcast", _username, message);
            AddMessage(new Message(_username, message, true));
            _newMessage = string.Empty;
        }
    }

    private class Message
    {
        public Message(string username, string body, bool mine)
        {
            Username = username;
            Body = body;
            Mine = mine;
        }

        public string Username { get; set; }
        public string Body { get; set; }
        public bool Mine { get; set; }

        public bool IsNotice => Body.StartsWith("[Notice]");

        public string CSS => Mine ? "sent" : "received";
    }
    private int ExecuteWrite(string query, Dictionary<string, object> args)
    {
        int numberOfRowsAffected;
        using (var con = new SQLiteConnection("Data Source = Messages.db"))
        {
            con.Open();
            using (var cmd = new SQLiteCommand(query, con))
            {
                foreach (var pair in args)
                {
                    cmd.Parameters.AddWithValue(pair.Key, pair.Value);
                }
                numberOfRowsAffected = cmd.ExecuteNonQuery();
            }
            return numberOfRowsAffected;
        }
    }
    private DataTable Execute(string query, Dictionary<string, object> args)
    {
        if (string.IsNullOrEmpty(query.Trim()))
            return null;

        using (var con = new SQLiteConnection("Data Source=test.db"))
        {
            con.Open();
            using (var cmd = new SQLiteCommand(query, con))
            {
                foreach (KeyValuePair<string, object> entry in args)
                {
                    cmd.Parameters.AddWithValue(entry.Key, entry.Value);
                }

                var da = new SQLiteDataAdapter(cmd);

                var dt = new DataTable();
                da.Fill(dt);

                da.Dispose();
                return dt;
            }
        }
    }
    private int AddMessage(Message message)
    {
        const string query = "INSERT INTO Messages(Username, Message) VALUES(@Username, @Body)";
        var args = new Dictionary<string, object>
        {
            {"@Username", message.Username},
            {"@Body", message.Body}
        };
        return ExecuteWrite(query, args);
    }
    private List<Message> GetMessages()
    {
        List<Message> messages = new List<Message>();
        const string query = "SELECT * FROM (SELECT * FROM Messages ORDER BY Field1 DESC LIMIT 10) Var1 ORDER BY Field1 ASC;";
        using var con = new SQLiteConnection(@"URI=file:/home/ovidiu/Documents/Projects/Auth/Messages.db");
        con.Open();
        using var cmd = new SQLiteCommand(query, con);
        using SQLiteDataReader rdr = cmd.ExecuteReader();
        while (rdr.Read())
        {
            if(rdr.GetString(1) == _username)
            {
                Message message = new Message(rdr.GetString(1), rdr.GetString(2), true);
                Console.WriteLine($"{rdr.GetInt32(0)} {rdr.GetString(1)} {rdr.GetString(2)}");
                messages.Add(message);
            }
            else
            {
                Message message = new Message(rdr.GetString(1), rdr.GetString(2), false);
                Console.WriteLine($"{rdr.GetInt32(0)} {rdr.GetString(1)} {rdr.GetString(2)}");
                messages.Add(message);
            }
            
        }
        return messages;
    }

